import React, { createContext, useContext, useState, useEffect } from 'react';

const ThemeContext = createContext();

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

const COLOR_SCHEMES = {
  'modern-indigo': {
    primary: '#6366f1',
    secondary: '#4f46e5',
    accent: '#818cf8',
    light: {
      background: '#ffffff',
      surface: '#f9fafb',
      text: '#111827',
    },
    dark: {
      background: '#111827',
      surface: '#1f2937',
      text: '#f9fafb',
    },
  },
  'royal-purple': {
    primary: '#8b5cf6',
    secondary: '#7c3aed',
    accent: '#a78bfa',
    light: {
      background: '#ffffff',
      surface: '#f9fafb',
      text: '#111827',
    },
    dark: {
      background: '#111827',
      surface: '#1f2937',
      text: '#f9fafb',
    },
  },
  'ocean-blue': {
    primary: '#3b82f6',
    secondary: '#2563eb',
    accent: '#60a5fa',
    light: {
      background: '#ffffff',
      surface: '#f9fafb',
      text: '#111827',
    },
    dark: {
      background: '#111827',
      surface: '#1f2937',
      text: '#f9fafb',
    },
  },
  'emerald-green': {
    primary: '#10b981',
    secondary: '#059669',
    accent: '#34d399',
    light: {
      background: '#ffffff',
      surface: '#f9fafb',
      text: '#111827',
    },
    dark: {
      background: '#111827',
      surface: '#1f2937',
      text: '#f9fafb',
    },
  },
  'sunset-rose': {
    primary: '#f43f5e',
    secondary: '#e11d48',
    accent: '#fb7185',
    light: {
      background: '#ffffff',
      surface: '#f9fafb',
      text: '#111827',
    },
    dark: {
      background: '#111827',
      surface: '#1f2937',
      text: '#f9fafb',
    },
  },
};

export const ThemeProvider = ({ children }) => {
  const [isDark, setIsDark] = useState(() => {
    const savedTheme = localStorage.getItem('theme-mode');
    if (savedTheme) {
      return savedTheme === 'dark';
    }
    return window.matchMedia('(prefers-color-scheme: dark)').matches;
  });

  const [colorScheme, setColorScheme] = useState(() => {
    return localStorage.getItem('color-scheme') || 'modern-indigo';
  });

  useEffect(() => {
    const root = window.document.documentElement;
    const colors = COLOR_SCHEMES[colorScheme];
    
    // Apply dark mode
    if (isDark) {
      root.classList.add('dark');
      localStorage.setItem('theme-mode', 'dark');
      
      // Apply dark theme colors
      root.style.setProperty('--color-background', colors.dark.background);
      root.style.setProperty('--color-surface', colors.dark.surface);
      root.style.setProperty('--color-text', colors.dark.text);
    } else {
      root.classList.remove('dark');
      localStorage.setItem('theme-mode', 'light');
      
      // Apply light theme colors
      root.style.setProperty('--color-background', colors.light.background);
      root.style.setProperty('--color-surface', colors.light.surface);
      root.style.setProperty('--color-text', colors.light.text);
    }

    // Apply color scheme
    root.style.setProperty('--color-primary', colors.primary);
    root.style.setProperty('--color-secondary', colors.secondary);
    root.style.setProperty('--color-accent', colors.accent);
    localStorage.setItem('color-scheme', colorScheme);

    // Apply transition classes
    root.classList.add('transition-colors', 'duration-200');
  }, [isDark, colorScheme]);

  const setTheme = (mode) => {
    if (mode === 'system') {
      const systemDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
      setIsDark(systemDark);
      localStorage.removeItem('theme-mode');
      
      // Listen for system theme changes
      const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');
      const handler = (e) => setIsDark(e.matches);
      mediaQuery.addEventListener('change', handler);
      return () => mediaQuery.removeEventListener('change', handler);
    } else {
      setIsDark(mode === 'dark');
    }
  };

  const toggleTheme = () => {
    setIsDark(prev => !prev);
  };

  return (
    <ThemeContext.Provider 
      value={{ 
        isDark, 
        toggleTheme, 
        setTheme,
        colorScheme,
        setColorScheme,
        colors: COLOR_SCHEMES[colorScheme]
      }}
    >
      {children}
    </ThemeContext.Provider>
  );
};

export default ThemeProvider;
