import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
import {
  SunIcon,
  MoonIcon,
  BellIcon,
  UserIcon,
  PaintBrushIcon,
  AcademicCapIcon,
  CheckCircleIcon,
} from '@heroicons/react/24/outline';

const Settings = () => {
  const { user, updatePreferences } = useAuth();
  const { isDark, toggleTheme } = useTheme();
  const [dailyGoal, setDailyGoal] = useState(user?.preferences?.dailyGoal || 10);
  const [showSaveSuccess, setShowSaveSuccess] = useState(false);
  const [isSaving, setIsSaving] = useState(false);

  const handleDailyGoalChange = async (e) => {
    const value = parseInt(e.target.value, 10);
    setDailyGoal(value);
    setIsSaving(true);
    try {
      await updatePreferences({ dailyGoal: value });
      setShowSaveSuccess(true);
      setTimeout(() => setShowSaveSuccess(false), 2000);
    } catch (error) {
      console.error('Failed to update daily goal:', error);
    } finally {
      setIsSaving(false);
    }
  };

  const SettingSection = ({ icon: Icon, title, description, children }) => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="p-6 bg-white dark:bg-gray-800 rounded-xl shadow-sm"
    >
      <div className="flex items-start space-x-4">
        <div className="p-3 bg-primary-100 dark:bg-primary-900 rounded-lg">
          <Icon className="w-6 h-6 text-primary-600 dark:text-primary-400" />
        </div>
        <div className="flex-1">
          <h3 className="text-lg font-medium text-gray-900 dark:text-white">
            {title}
          </h3>
          <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
            {description}
          </p>
          <div className="mt-4">{children}</div>
        </div>
      </div>
    </motion.div>
  );

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="space-y-6"
      >
        <div className="flex justify-between items-center">
          <h2 className="text-3xl font-bold text-gray-900 dark:text-white">
            Settings
          </h2>
          {showSaveSuccess && (
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 20 }}
              className="flex items-center text-green-500 dark:text-green-400"
            >
              <CheckCircleIcon className="w-5 h-5 mr-2" />
              <span>Settings saved!</span>
            </motion.div>
          )}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Appearance Settings */}
          <SettingSection
            icon={PaintBrushIcon}
            title="Appearance"
            description="Customize how the app looks"
          >
            <div className="flex items-center justify-between p-4 bg-gray-50 dark:bg-gray-700 rounded-lg">
              <div>
                <p className="font-medium text-gray-900 dark:text-white">
                  Dark Mode
                </p>
                <p className="text-sm text-gray-500 dark:text-gray-400">
                  {isDark ? 'Currently using dark theme' : 'Currently using light theme'}
                </p>
              </div>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={toggleTheme}
                className="p-2 rounded-lg bg-white dark:bg-gray-600 shadow-sm hover:bg-gray-50 dark:hover:bg-gray-500 transition-colors"
              >
                {isDark ? (
                  <SunIcon className="w-6 h-6 text-yellow-500" />
                ) : (
                  <MoonIcon className="w-6 h-6 text-gray-500" />
                )}
              </motion.button>
            </div>
          </SettingSection>

          {/* Study Goals Settings */}
          <SettingSection
            icon={AcademicCapIcon}
            title="Study Goals"
            description="Set your daily learning targets"
          >
            <div className="p-4 bg-gray-50 dark:bg-gray-700 rounded-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="font-medium text-gray-900 dark:text-white">
                    Daily Review Goal
                  </p>
                  <p className="text-sm text-gray-500 dark:text-gray-400">
                    Cards to review each day
                  </p>
                </div>
                <select
                  value={dailyGoal}
                  onChange={handleDailyGoalChange}
                  disabled={isSaving}
                  className="ml-4 block rounded-lg border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow-sm focus:border-primary-500 focus:ring-primary-500"
                >
                  {[5, 10, 15, 20, 25, 30].map((num) => (
                    <option key={num} value={num}>
                      {num} cards
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </SettingSection>

          {/* Notifications Settings */}
          <SettingSection
            icon={BellIcon}
            title="Notifications"
            description="Manage your notification preferences"
          >
            <div className="space-y-4">
              <div className="flex items-center">
                <input
                  type="checkbox"
                  id="dailyReminder"
                  className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                />
                <label
                  htmlFor="dailyReminder"
                  className="ml-3 text-gray-900 dark:text-white"
                >
                  Daily review reminder
                </label>
              </div>
              <div className="flex items-center">
                <input
                  type="checkbox"
                  id="achievementNotif"
                  className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                />
                <label
                  htmlFor="achievementNotif"
                  className="ml-3 text-gray-900 dark:text-white"
                >
                  Achievement notifications
                </label>
              </div>
            </div>
          </SettingSection>

          {/* Account Settings */}
          <SettingSection
            icon={UserIcon}
            title="Account"
            description="Manage your account settings"
          >
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-500 dark:text-gray-400">
                  Email
                </label>
                <p className="mt-1 text-gray-900 dark:text-white">{user?.email}</p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-500 dark:text-gray-400">
                  Username
                </label>
                <p className="mt-1 text-gray-900 dark:text-white">
                  {user?.username}
                </p>
              </div>
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                className="mt-4 w-full px-4 py-2 bg-red-100 text-red-600 rounded-lg hover:bg-red-200 transition-colors"
              >
                Delete Account
              </motion.button>
            </div>
          </SettingSection>
        </div>
      </motion.div>
    </div>
  );
};

export default Settings;
