import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { motion } from 'framer-motion';
import {
  AcademicCapIcon,
  PlusIcon,
  ChartBarIcon,
  ClockIcon,
} from '@heroicons/react/24/outline';

const Dashboard = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const response = await axios.get('/api/flashcards/stats/summary');
      setStats(response.data);
    } catch (err) {
      setError('Failed to load statistics');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-[50vh] flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-[50vh] flex items-center justify-center">
        <div className="text-red-500">{error}</div>
      </div>
    );
  }

  const quickActions = [
    {
      title: 'Start Review',
      description: 'Review your due flashcards',
      icon: AcademicCapIcon,
      to: '/review',
      color: 'bg-green-500',
    },
    {
      title: 'Create Flashcard',
      description: 'Add a new flashcard to your collection',
      icon: PlusIcon,
      to: '/create',
      color: 'bg-blue-500',
    },
    {
      title: 'View Statistics',
      description: 'Track your learning progress',
      icon: ChartBarIcon,
      to: '/statistics',
      color: 'bg-purple-500',
    },
  ];

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <div className="text-center">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
          Welcome to Your Flashcards
        </h1>
        <p className="mt-2 text-gray-600 dark:text-gray-400">
          Track your progress and keep learning
        </p>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600 dark:text-gray-400">Total Flashcards</p>
              <p className="text-2xl font-semibold text-gray-900 dark:text-white">
                {stats?.totalCards || 0}
              </p>
            </div>
            <div className="p-3 bg-blue-100 dark:bg-blue-900 rounded-full">
              <AcademicCapIcon className="w-6 h-6 text-blue-500 dark:text-blue-400" />
            </div>
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
          className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600 dark:text-gray-400">Due Today</p>
              <p className="text-2xl font-semibold text-gray-900 dark:text-white">
                {stats?.dueToday || 0}
              </p>
            </div>
            <div className="p-3 bg-green-100 dark:bg-green-900 rounded-full">
              <ClockIcon className="w-6 h-6 text-green-500 dark:text-green-400" />
            </div>
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600 dark:text-gray-400">Mastered Cards</p>
              <p className="text-2xl font-semibold text-gray-900 dark:text-white">
                {stats?.boxStats?.[4]?.count || 0}
              </p>
            </div>
            <div className="p-3 bg-purple-100 dark:bg-purple-900 rounded-full">
              <ChartBarIcon className="w-6 h-6 text-purple-500 dark:text-purple-400" />
            </div>
          </div>
        </motion.div>
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {quickActions.map((action, index) => (
          <motion.div
            key={action.title}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
          >
            <Link
              to={action.to}
              className="block h-full p-6 bg-white dark:bg-gray-800 rounded-lg shadow-md hover:shadow-lg transition-shadow"
            >
              <div className="flex items-center space-x-4">
                <div className={`p-3 ${action.color} rounded-full`}>
                  <action.icon className="w-6 h-6 text-white" />
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                    {action.title}
                  </h3>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    {action.description}
                  </p>
                </div>
              </div>
            </Link>
          </motion.div>
        ))}
      </div>

      {/* Box Distribution */}
      <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md">
        <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
          Learning Progress
        </h3>
        <div className="grid grid-cols-5 gap-4">
          {stats?.boxStats?.map((box) => (
            <div key={box.box} className="text-center">
              <div className="mb-2">
                <div
                  className="mx-auto h-20 w-4 bg-primary-200 dark:bg-primary-900 rounded-full overflow-hidden"
                  style={{
                    background: `linear-gradient(to top, #3B82F6 ${
                      (box.count / (stats.totalCards || 1)) * 100
                    }%, #E5E7EB ${(box.count / (stats.totalCards || 1)) * 100}%)`,
                  }}
                />
              </div>
              <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                Box {box.box}
              </p>
              <p className="text-xs text-gray-500 dark:text-gray-500">{box.count}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
