import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { motion, AnimatePresence } from 'framer-motion';
import {
  ArrowPathIcon,
  CheckIcon,
  XMarkIcon,
} from '@heroicons/react/24/outline';

const Review = () => {
  const [flashcards, setFlashcards] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isFlipped, setIsFlipped] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchDueFlashcards();
  }, []);

  const fetchDueFlashcards = async () => {
    try {
      const response = await axios.get('/api/flashcards/due');
      setFlashcards(response.data);
    } catch (err) {
      setError('Failed to load flashcards');
    } finally {
      setLoading(false);
    }
  };

  const handleAnswer = async (isCorrect) => {
    try {
      const currentCard = flashcards[currentIndex];
      await axios.patch(`/api/flashcards/${currentCard._id}/review`, {
        isCorrect,
      });

      // Move to next card
      if (currentIndex < flashcards.length - 1) {
        setCurrentIndex(prev => prev + 1);
      } else {
        // Finished reviewing all cards
        navigate('/');
      }
      setIsFlipped(false);
    } catch (err) {
      setError('Failed to update flashcard');
    }
  };

  if (loading) {
    return (
      <div className="min-h-[50vh] flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-[50vh] flex items-center justify-center">
        <div className="text-red-500">{error}</div>
      </div>
    );
  }

  if (flashcards.length === 0) {
    return (
      <div className="min-h-[50vh] flex flex-col items-center justify-center">
        <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">
          No cards due for review!
        </h2>
        <p className="text-gray-600 dark:text-gray-400 mb-8">
          Great job! You've completed all your reviews for now.
        </p>
        <button
          onClick={() => navigate('/')}
          className="bg-primary-500 hover:bg-primary-600 text-white px-6 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2"
        >
          Back to Dashboard
        </button>
      </div>
    );
  }

  const currentCard = flashcards[currentIndex];
  const progress = ((currentIndex + 1) / flashcards.length) * 100;

  return (
    <div className="max-w-2xl mx-auto">
      {/* Progress Bar */}
      <div className="mb-8">
        <div className="flex justify-between text-sm text-gray-600 dark:text-gray-400 mb-2">
          <span>Progress</span>
          <span>{`${currentIndex + 1} / ${flashcards.length}`}</span>
        </div>
        <div className="h-2 bg-gray-200 dark:bg-gray-700 rounded-full">
          <motion.div
            className="h-full bg-primary-500 rounded-full"
            initial={{ width: 0 }}
            animate={{ width: `${progress}%` }}
            transition={{ duration: 0.3 }}
          />
        </div>
      </div>

      {/* Flashcard */}
      <div className="perspective-1000">
        <AnimatePresence mode="wait">
          <motion.div
            key={currentCard._id + (isFlipped ? '-flipped' : '')}
            initial={{ rotateY: isFlipped ? -180 : 0, opacity: 0 }}
            animate={{ rotateY: isFlipped ? -180 : 0, opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.3 }}
            className="relative w-full aspect-[3/2] cursor-pointer"
            onClick={() => setIsFlipped(!isFlipped)}
          >
            <div
              className={`absolute inset-0 backface-hidden rounded-xl p-8 flex flex-col items-center justify-center text-center bg-white dark:bg-gray-800 shadow-lg ${
                isFlipped ? 'hidden' : ''
              }`}
            >
              <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Question
              </h3>
              <p className="text-gray-700 dark:text-gray-300 text-lg">
                {currentCard.question}
              </p>
            </div>
            <div
              className={`absolute inset-0 backface-hidden rounded-xl p-8 flex flex-col items-center justify-center text-center bg-white dark:bg-gray-800 shadow-lg transform rotate-y-180 ${
                !isFlipped ? 'hidden' : ''
              }`}
            >
              <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Answer
              </h3>
              <p className="text-gray-700 dark:text-gray-300 text-lg">
                {currentCard.answer}
              </p>
            </div>
          </motion.div>
        </AnimatePresence>
      </div>

      {/* Controls */}
      <div className="mt-8 flex justify-center space-x-4">
        <button
          onClick={() => setIsFlipped(!isFlipped)}
          className="p-3 rounded-full bg-gray-100 dark:bg-gray-700 hover:bg-gray-200 dark:hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-primary-500"
        >
          <ArrowPathIcon className="w-6 h-6 text-gray-600 dark:text-gray-400" />
        </button>
        {isFlipped && (
          <>
            <button
              onClick={() => handleAnswer(false)}
              className="p-3 rounded-full bg-red-100 dark:bg-red-900 hover:bg-red-200 dark:hover:bg-red-800 focus:outline-none focus:ring-2 focus:ring-red-500"
            >
              <XMarkIcon className="w-6 h-6 text-red-600 dark:text-red-400" />
            </button>
            <button
              onClick={() => handleAnswer(true)}
              className="p-3 rounded-full bg-green-100 dark:bg-green-900 hover:bg-green-200 dark:hover:bg-green-800 focus:outline-none focus:ring-2 focus:ring-green-500"
            >
              <CheckIcon className="w-6 h-6 text-green-600 dark:text-green-400" />
            </button>
          </>
        )}
      </div>

      {/* Box Info */}
      <div className="mt-8 text-center text-sm text-gray-600 dark:text-gray-400">
        Currently in Box {currentCard.box}
      </div>
    </div>
  );
};

export default Review;
