import React, { createContext, useContext, useState, useEffect } from 'react';

const ThemeContext = createContext();

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

export const ThemeProvider = ({ children }) => {
  const [isDark, setIsDark] = useState(() => {
    const savedTheme = localStorage.getItem('theme-mode');
    if (savedTheme) {
      return savedTheme === 'dark';
    }
    return window.matchMedia('(prefers-color-scheme: dark)').matches;
  });

  const [colorScheme, setColorScheme] = useState(() => {
    return localStorage.getItem('color-scheme') || 'indigo';
  });

  useEffect(() => {
    const root = window.document.documentElement;
    
    // Apply dark mode
    if (isDark) {
      root.classList.add('dark');
      localStorage.setItem('theme-mode', 'dark');
    } else {
      root.classList.remove('dark');
      localStorage.setItem('theme-mode', 'light');
    }

    // Apply color scheme
    const colors = {
      indigo: {
        primary: '#6366f1',
        secondary: '#4f46e5',
        accent: '#818cf8'
      },
      violet: {
        primary: '#8b5cf6',
        secondary: '#7c3aed',
        accent: '#a78bfa'
      },
      blue: {
        primary: '#3b82f6',
        secondary: '#2563eb',
        accent: '#60a5fa'
      },
      emerald: {
        primary: '#10b981',
        secondary: '#059669',
        accent: '#34d399'
      },
      rose: {
        primary: '#f43f5e',
        secondary: '#e11d48',
        accent: '#fb7185'
      }
    };

    const selectedColors = colors[colorScheme];
    root.style.setProperty('--color-primary', selectedColors.primary);
    root.style.setProperty('--color-secondary', selectedColors.secondary);
    root.style.setProperty('--color-accent', selectedColors.accent);
    localStorage.setItem('color-scheme', colorScheme);
  }, [isDark, colorScheme]);

  const setTheme = (mode) => {
    if (mode === 'system') {
      const systemDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
      setIsDark(systemDark);
      localStorage.removeItem('theme-mode');
    } else {
      setIsDark(mode === 'dark');
    }
  };

  const toggleTheme = () => {
    setIsDark(prev => !prev);
  };

  return (
    <ThemeContext.Provider 
      value={{ 
        isDark, 
        toggleTheme, 
        setTheme,
        colorScheme,
        setColorScheme
      }}
    >
      {children}
    </ThemeContext.Provider>
  );
};

export default ThemeProvider;
