import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useTheme } from '../context/ThemeContext';
import {
  SunIcon,
  MoonIcon,
  ComputerDesktopIcon,
  SwatchIcon,
  CheckIcon,
  BeakerIcon,
  SparklesIcon,
} from '@heroicons/react/24/outline';

const ThemeSelector = () => {
  const { isDark, setTheme, colorScheme, setColorScheme } = useTheme();
  const [activeTab, setActiveTab] = useState('theme');
  const [previewMode, setPreviewMode] = useState(null);

  const tabs = [
    { id: 'theme', name: 'Theme Mode', icon: SunIcon },
    { id: 'color', name: 'Color Scheme', icon: SwatchIcon },
    { id: 'preview', name: 'Live Preview', icon: BeakerIcon },
  ];

  const themes = [
    {
      id: 'light',
      name: 'Light Mode',
      icon: SunIcon,
      description: 'Clean and bright interface',
      preview: (
        <div className="absolute inset-0 bg-gradient-to-br from-white via-gray-50 to-gray-100">
          <div className="absolute inset-0 bg-white/40 backdrop-blur-sm" />
          <div className="absolute inset-x-0 top-1/3 flex justify-center">
            <div className="w-16 h-16 bg-yellow-400/20 rounded-full flex items-center justify-center">
              <SunIcon className="w-8 h-8 text-yellow-500" />
            </div>
          </div>
        </div>
      ),
    },
    {
      id: 'dark',
      name: 'Dark Mode',
      icon: MoonIcon,
      description: 'Easy on the eyes',
      preview: (
        <div className="absolute inset-0 bg-gradient-to-br from-gray-900 via-gray-800 to-gray-700">
          <div className="absolute inset-0 bg-black/20 backdrop-blur-sm" />
          <div className="absolute inset-x-0 top-1/3 flex justify-center">
            <div className="w-16 h-16 bg-indigo-500/20 rounded-full flex items-center justify-center">
              <MoonIcon className="w-8 h-8 text-indigo-400" />
            </div>
          </div>
        </div>
      ),
    },
    {
      id: 'system',
      name: 'System Mode',
      icon: ComputerDesktopIcon,
      description: 'Follows your system',
      preview: (
        <div className="absolute inset-0">
          <div className="absolute inset-0 bg-gradient-to-br from-gray-100 to-gray-900" />
          <div className="absolute inset-0 bg-black/10 backdrop-blur-sm" />
          <div className="absolute inset-x-0 top-1/3 flex justify-center">
            <div className="w-16 h-16 bg-purple-500/20 rounded-full flex items-center justify-center">
              <ComputerDesktopIcon className="w-8 h-8 text-purple-400" />
            </div>
          </div>
        </div>
      ),
    },
  ];

  const colorSchemes = [
    {
      id: 'indigo',
      name: 'Modern Indigo',
      colors: ['bg-indigo-400', 'bg-indigo-500', 'bg-indigo-600'],
      gradient: 'from-indigo-500 via-indigo-600 to-indigo-700',
      accent: 'bg-indigo-500',
    },
    {
      id: 'purple',
      name: 'Royal Purple',
      colors: ['bg-purple-400', 'bg-purple-500', 'bg-purple-600'],
      gradient: 'from-purple-500 via-purple-600 to-purple-700',
      accent: 'bg-purple-500',
    },
    {
      id: 'blue',
      name: 'Ocean Blue',
      colors: ['bg-blue-400', 'bg-blue-500', 'bg-blue-600'],
      gradient: 'from-blue-500 via-blue-600 to-blue-700',
      accent: 'bg-blue-500',
    },
    {
      id: 'emerald',
      name: 'Emerald Green',
      colors: ['bg-emerald-400', 'bg-emerald-500', 'bg-emerald-600'],
      gradient: 'from-emerald-500 via-emerald-600 to-emerald-700',
      accent: 'bg-emerald-500',
    },
    {
      id: 'rose',
      name: 'Sunset Rose',
      colors: ['bg-rose-400', 'bg-rose-500', 'bg-rose-600'],
      gradient: 'from-rose-500 via-rose-600 to-rose-700',
      accent: 'bg-rose-500',
    },
  ];

  return (
    <div className="relative">
      {/* Tabs */}
      <div className="flex space-x-1 bg-gray-100 dark:bg-gray-800 p-1 rounded-lg mb-8">
        {tabs.map((tab) => (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id)}
            className={`flex-1 flex items-center justify-center space-x-2 px-4 py-2.5 rounded-md text-sm font-medium transition-all duration-200 ${
              activeTab === tab.id
                ? 'bg-white dark:bg-gray-700 text-gray-900 dark:text-white shadow-sm'
                : 'text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300'
            }`}
          >
            <tab.icon className="w-5 h-5" />
            <span>{tab.name}</span>
          </button>
        ))}
      </div>

      <AnimatePresence mode="wait">
        {activeTab === 'theme' && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="grid grid-cols-1 md:grid-cols-3 gap-6"
          >
            {themes.map((theme) => (
              <motion.div
                key={theme.id}
                className={`relative overflow-hidden rounded-2xl aspect-[4/3] cursor-pointer group ${
                  (theme.id === 'dark' && isDark) ||
                  (theme.id === 'light' && !isDark)
                    ? 'ring-2 ring-primary-500'
                    : 'hover:ring-2 hover:ring-primary-500/50'
                }`}
                onClick={() => setTheme(theme.id)}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                {theme.preview}
                <div className="absolute inset-x-0 bottom-0 p-4 bg-gradient-to-t from-black/60 via-black/40 to-transparent">
                  <div className="flex items-center justify-between">
                    <div>
                      <h4 className="text-white font-semibold">{theme.name}</h4>
                      <p className="text-white/80 text-sm">{theme.description}</p>
                    </div>
                    {((theme.id === 'dark' && isDark) ||
                      (theme.id === 'light' && !isDark)) && (
                      <div className="bg-primary-500 rounded-full p-1">
                        <CheckIcon className="w-4 h-4 text-white" />
                      </div>
                    )}
                  </div>
                </div>
              </motion.div>
            ))}
          </motion.div>
        )}

        {activeTab === 'color' && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="grid grid-cols-2 md:grid-cols-5 gap-6"
          >
            {colorSchemes.map((scheme) => (
              <motion.div
                key={scheme.id}
                className={`group cursor-pointer ${
                  colorScheme === scheme.id
                    ? 'ring-2 ring-primary-500 ring-offset-4 dark:ring-offset-gray-900'
                    : ''
                }`}
                onClick={() => setColorScheme(scheme.id)}
                whileHover={{ y: -4 }}
                whileTap={{ scale: 0.98 }}
              >
                <div className="relative aspect-square rounded-2xl overflow-hidden">
                  <div className={`absolute inset-0 bg-gradient-to-br ${scheme.gradient}`} />
                  <div className="absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                    <div className="h-full flex flex-col items-center justify-center bg-black/20 backdrop-blur-sm">
                      <SparklesIcon className="w-8 h-8 text-white mb-2" />
                      <span className="text-white font-medium">{scheme.name}</span>
                      <div className="mt-4 flex space-x-2">
                        {scheme.colors.map((color, index) => (
                          <motion.div
                            key={index}
                            initial={{ scale: 0 }}
                            animate={{ scale: 1 }}
                            transition={{ delay: index * 0.1 }}
                            className={`w-3 h-3 rounded-full ${color}`}
                          />
                        ))}
                      </div>
                    </div>
                  </div>
                  {colorScheme === scheme.id && (
                    <motion.div
                      initial={{ scale: 0 }}
                      animate={{ scale: 1 }}
                      className="absolute top-2 right-2 bg-white dark:bg-gray-800 rounded-full p-1.5 shadow-lg"
                    >
                      <CheckIcon className="w-4 h-4 text-primary-500" />
                    </motion.div>
                  )}
                </div>
              </motion.div>
            ))}
          </motion.div>
        )}

        {activeTab === 'preview' && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="bg-white dark:bg-gray-800 rounded-2xl overflow-hidden shadow-xl"
          >
            <div className="p-6 space-y-6">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                  Preview Your Theme
                </h3>
                <div className={`px-3 py-1 rounded-full ${colorScheme === 'indigo' ? 'bg-indigo-100 text-indigo-700' : colorScheme === 'purple' ? 'bg-purple-100 text-purple-700' : colorScheme === 'blue' ? 'bg-blue-100 text-blue-700' : colorScheme === 'emerald' ? 'bg-emerald-100 text-emerald-700' : 'bg-rose-100 text-rose-700'}`}>
                  {isDark ? 'Dark Mode' : 'Light Mode'}
                </div>
              </div>
              
              <div className="grid grid-cols-3 gap-4">
                <div className="space-y-2">
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded-full w-3/4 animate-pulse" />
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded-full w-1/2 animate-pulse" />
                </div>
                <div className="space-y-2">
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded-full w-full animate-pulse" />
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded-full w-2/3 animate-pulse" />
                </div>
                <div className="space-y-2">
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded-full w-1/2 animate-pulse" />
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded-full w-3/4 animate-pulse" />
                </div>
              </div>

              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <div className="w-10 h-10 rounded-full bg-primary-500 animate-pulse" />
                  <div className="space-y-2">
                    <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded-full w-24 animate-pulse" />
                    <div className="h-3 bg-gray-200 dark:bg-gray-700 rounded-full w-32 animate-pulse" />
                  </div>
                </div>
                <div className="flex space-x-2">
                  {[1, 2, 3].map((_, i) => (
                    <div
                      key={i}
                      className={`w-8 h-8 rounded-lg ${
                        colorScheme === 'indigo'
                          ? 'bg-indigo-500'
                          : colorScheme === 'purple'
                          ? 'bg-purple-500'
                          : colorScheme === 'blue'
                          ? 'bg-blue-500'
                          : colorScheme === 'emerald'
                          ? 'bg-emerald-500'
                          : 'bg-rose-500'
                      } animate-pulse`}
                    />
                  ))}
                </div>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default ThemeSelector;
