import React from 'react';
import { motion } from 'framer-motion';
import { useTheme } from '../context/ThemeContext';
import {
  SunIcon,
  MoonIcon,
  ComputerDesktopIcon,
  SwatchIcon,
} from '@heroicons/react/24/outline';

const ThemeSelector = () => {
  const { isDark, toggleTheme, setTheme } = useTheme();

  const themes = [
    {
      id: 'light',
      name: 'Light',
      icon: SunIcon,
      description: 'Clean and bright interface',
      colors: ['bg-white', 'bg-primary-50', 'bg-gray-50'],
    },
    {
      id: 'dark',
      name: 'Dark',
      icon: MoonIcon,
      description: 'Easy on the eyes',
      colors: ['bg-gray-900', 'bg-gray-800', 'bg-gray-700'],
    },
    {
      id: 'system',
      name: 'System',
      icon: ComputerDesktopIcon,
      description: 'Follows system preference',
      colors: ['bg-gradient-to-r from-gray-100 to-gray-900'],
    },
  ];

  const colorSchemes = [
    {
      id: 'indigo',
      name: 'Indigo',
      primary: 'bg-indigo-500',
      secondary: 'bg-indigo-600',
      accent: 'bg-indigo-400',
    },
    {
      id: 'violet',
      name: 'Violet',
      primary: 'bg-violet-500',
      secondary: 'bg-violet-600',
      accent: 'bg-violet-400',
    },
    {
      id: 'blue',
      name: 'Ocean',
      primary: 'bg-blue-500',
      secondary: 'bg-blue-600',
      accent: 'bg-blue-400',
    },
    {
      id: 'emerald',
      name: 'Forest',
      primary: 'bg-emerald-500',
      secondary: 'bg-emerald-600',
      accent: 'bg-emerald-400',
    },
    {
      id: 'rose',
      name: 'Rose',
      primary: 'bg-rose-500',
      secondary: 'bg-rose-600',
      accent: 'bg-rose-400',
    },
  ];

  return (
    <div className="space-y-8">
      {/* Theme Mode Selection */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {themes.map((theme) => (
          <motion.button
            key={theme.id}
            onClick={() => setTheme(theme.id)}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            className={`p-4 rounded-xl border-2 transition-colors ${
              (theme.id === 'dark' && isDark) ||
              (theme.id === 'light' && !isDark)
                ? 'border-primary-500 bg-primary-50 dark:bg-primary-900/20'
                : 'border-gray-200 dark:border-gray-700 hover:border-primary-500 dark:hover:border-primary-500'
            }`}
          >
            <div className="flex items-center space-x-4">
              <div className="p-3 bg-primary-100 dark:bg-primary-900/50 rounded-lg">
                <theme.icon className="w-6 h-6 text-primary-600 dark:text-primary-400" />
              </div>
              <div className="flex-1 text-left">
                <h3 className="font-medium text-gray-900 dark:text-white">
                  {theme.name}
                </h3>
                <p className="text-sm text-gray-500 dark:text-gray-400">
                  {theme.description}
                </p>
              </div>
            </div>
            <div className="mt-4 flex space-x-2">
              {theme.colors.map((color, index) => (
                <div
                  key={index}
                  className={`w-8 h-8 rounded-full ${color}`}
                />
              ))}
            </div>
          </motion.button>
        ))}
      </div>

      {/* Color Scheme Selection */}
      <div>
        <div className="flex items-center space-x-2 mb-4">
          <SwatchIcon className="w-5 h-5 text-primary-500" />
          <h3 className="text-lg font-medium text-gray-900 dark:text-white">
            Color Scheme
          </h3>
        </div>
        <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
          {colorSchemes.map((scheme) => (
            <motion.button
              key={scheme.id}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.98 }}
              className="group relative aspect-square rounded-xl overflow-hidden"
            >
              <div className={`absolute inset-0 ${scheme.primary}`} />
              <div
                className={`absolute bottom-0 left-0 right-0 h-1/2 bg-gradient-to-br ${scheme.secondary} ${scheme.accent}`}
              />
              <div className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity bg-black/20">
                <span className="text-white font-medium">{scheme.name}</span>
              </div>
            </motion.button>
          ))}
        </div>
      </div>

      {/* Preview Section */}
      <div className="mt-8">
        <div className="flex items-center space-x-2 mb-4">
          <ComputerDesktopIcon className="w-5 h-5 text-primary-500" />
          <h3 className="text-lg font-medium text-gray-900 dark:text-white">
            Preview
          </h3>
        </div>
        <div className="relative h-48 rounded-xl overflow-hidden border border-gray-200 dark:border-gray-700">
          <div className="absolute inset-0 bg-gradient-to-br from-primary-500 to-primary-600" />
          <div className="absolute inset-0 bg-white dark:bg-gray-800 opacity-95">
            <div className="p-4">
              <div className="flex items-center justify-between mb-4">
                <div className="w-24 h-8 bg-gray-200 dark:bg-gray-700 rounded animate-pulse" />
                <div className="w-8 h-8 bg-gray-200 dark:bg-gray-700 rounded-full animate-pulse" />
              </div>
              <div className="space-y-2">
                <div className="w-3/4 h-4 bg-gray-200 dark:bg-gray-700 rounded animate-pulse" />
                <div className="w-1/2 h-4 bg-gray-200 dark:bg-gray-700 rounded animate-pulse" />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ThemeSelector;
