import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useTheme } from '../context/ThemeContext';
import {
  SunIcon,
  MoonIcon,
  ComputerDesktopIcon,
  SwatchIcon,
  CheckIcon,
} from '@heroicons/react/24/outline';

const ThemeSelector = () => {
  const { isDark, setTheme, colorScheme, setColorScheme } = useTheme();
  const [selectedPreview, setSelectedPreview] = useState(null);

  const themes = [
    {
      id: 'light',
      name: 'Light Theme',
      icon: SunIcon,
      description: 'Perfect for daytime use',
      preview: 'bg-gradient-to-br from-white via-gray-50 to-gray-100',
      accent: 'bg-yellow-400',
    },
    {
      id: 'dark',
      name: 'Dark Theme',
      icon: MoonIcon,
      description: 'Easy on the eyes at night',
      preview: 'bg-gradient-to-br from-gray-900 via-gray-800 to-gray-700',
      accent: 'bg-indigo-500',
    },
    {
      id: 'system',
      name: 'System Theme',
      icon: ComputerDesktopIcon,
      description: 'Adapts to your device',
      preview: 'bg-gradient-to-br from-gray-100 via-gray-300 to-gray-900',
      accent: 'bg-purple-500',
    },
  ];

  const colorSchemes = [
    {
      id: 'indigo',
      name: 'Modern Indigo',
      gradient: 'from-indigo-500 via-indigo-600 to-indigo-700',
      shadow: 'shadow-indigo-500/30',
      demo: [
        'bg-indigo-400',
        'bg-indigo-500',
        'bg-indigo-600',
      ],
    },
    {
      id: 'purple',
      name: 'Royal Purple',
      gradient: 'from-purple-500 via-purple-600 to-purple-700',
      shadow: 'shadow-purple-500/30',
      demo: [
        'bg-purple-400',
        'bg-purple-500',
        'bg-purple-600',
      ],
    },
    {
      id: 'blue',
      name: 'Ocean Blue',
      gradient: 'from-blue-500 via-blue-600 to-blue-700',
      shadow: 'shadow-blue-500/30',
      demo: [
        'bg-blue-400',
        'bg-blue-500',
        'bg-blue-600',
      ],
    },
    {
      id: 'emerald',
      name: 'Emerald Green',
      gradient: 'from-emerald-500 via-emerald-600 to-emerald-700',
      shadow: 'shadow-emerald-500/30',
      demo: [
        'bg-emerald-400',
        'bg-emerald-500',
        'bg-emerald-600',
      ],
    },
    {
      id: 'rose',
      name: 'Sunset Rose',
      gradient: 'from-rose-500 via-rose-600 to-rose-700',
      shadow: 'shadow-rose-500/30',
      demo: [
        'bg-rose-400',
        'bg-rose-500',
        'bg-rose-600',
      ],
    },
  ];

  return (
    <div className="space-y-12">
      {/* Theme Mode Selection */}
      <div>
        <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-6 flex items-center">
          <SunIcon className="w-5 h-5 mr-2 text-primary-500" />
          Choose Theme
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {themes.map((theme) => (
            <motion.button
              key={theme.id}
              onClick={() => setTheme(theme.id)}
              whileHover={{ y: -4, scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              className={`relative group overflow-hidden rounded-2xl ${theme.preview} shadow-lg transition-all duration-300 ${
                (theme.id === 'dark' && isDark) ||
                (theme.id === 'light' && !isDark)
                  ? 'ring-2 ring-primary-500 ring-offset-2 dark:ring-offset-gray-900'
                  : 'hover:ring-2 hover:ring-primary-500/50 hover:ring-offset-2 dark:hover:ring-offset-gray-900'
              }`}
            >
              <div className="aspect-[4/3] p-6 flex flex-col justify-between">
                <div className="flex justify-between items-start">
                  <div className={`p-3 rounded-xl ${theme.accent} bg-opacity-20`}>
                    <theme.icon className="w-6 h-6 text-gray-900 dark:text-white" />
                  </div>
                  {((theme.id === 'dark' && isDark) ||
                    (theme.id === 'light' && !isDark)) && (
                    <motion.div
                      initial={{ scale: 0 }}
                      animate={{ scale: 1 }}
                      className="bg-primary-500 rounded-full p-1"
                    >
                      <CheckIcon className="w-4 h-4 text-white" />
                    </motion.div>
                  )}
                </div>
                <div className="text-left">
                  <h4 className="font-semibold text-gray-900 dark:text-white mb-1">
                    {theme.name}
                  </h4>
                  <p className="text-sm text-gray-700 dark:text-gray-300">
                    {theme.description}
                  </p>
                </div>
              </div>
            </motion.button>
          ))}
        </div>
      </div>

      {/* Color Schemes */}
      <div>
        <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-6 flex items-center">
          <SwatchIcon className="w-5 h-5 mr-2 text-primary-500" />
          Color Scheme
        </h3>
        <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
          {colorSchemes.map((scheme) => (
            <motion.button
              key={scheme.id}
              onClick={() => setColorScheme(scheme.id)}
              onMouseEnter={() => setSelectedPreview(scheme.id)}
              onMouseLeave={() => setSelectedPreview(null)}
              whileHover={{ y: -4 }}
              whileTap={{ scale: 0.98 }}
              className={`group relative rounded-xl overflow-hidden ${scheme.shadow} transition-all duration-300 ${
                colorScheme === scheme.id
                  ? 'ring-2 ring-primary-500 ring-offset-2 dark:ring-offset-gray-900'
                  : ''
              }`}
            >
              <div className="aspect-square">
                <div className={`absolute inset-0 bg-gradient-to-br ${scheme.gradient}`} />
                <div className="absolute inset-0 bg-black/20 opacity-0 group-hover:opacity-100 transition-opacity">
                  <div className="h-full flex flex-col items-center justify-center">
                    <span className="text-white font-medium text-sm">
                      {scheme.name}
                    </span>
                    <div className="mt-3 flex space-x-2">
                      {scheme.demo.map((color, index) => (
                        <motion.div
                          key={index}
                          initial={{ scale: 0 }}
                          animate={{ scale: 1 }}
                          transition={{ delay: index * 0.1 }}
                          className={`w-3 h-3 rounded-full ${color}`}
                        />
                      ))}
                    </div>
                  </div>
                </div>
                {colorScheme === scheme.id && (
                  <div className="absolute top-2 right-2 bg-white dark:bg-gray-800 rounded-full p-1">
                    <CheckIcon className="w-4 h-4 text-primary-500" />
                  </div>
                )}
              </div>
            </motion.button>
          ))}
        </div>
      </div>

      {/* Live Preview */}
      <AnimatePresence mode="wait">
        {selectedPreview && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            className="fixed bottom-8 right-8 p-4 bg-white dark:bg-gray-800 rounded-lg shadow-xl border border-gray-200 dark:border-gray-700"
          >
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 rounded-full bg-primary-500" />
              <span className="text-sm font-medium text-gray-900 dark:text-white">
                Preview: {colorSchemes.find(s => s.id === selectedPreview)?.name}
              </span>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default ThemeSelector;
