import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import {
  AcademicCapIcon,
  ClockIcon,
  FireIcon,
  TrophyIcon,
  CalendarIcon,
  ChartBarIcon,
} from '@heroicons/react/24/outline';

const Profile = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedTab, setSelectedTab] = useState('overview');

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const response = await axios.get('/api/flashcards/stats/summary');
      setStats(response.data);
    } catch (error) {
      console.error('Failed to fetch stats:', error);
    } finally {
      setLoading(false);
    }
  };

  const achievements = [
    {
      title: 'Quick Learner',
      description: 'Created first 10 flashcards',
      icon: AcademicCapIcon,
      earned: stats?.totalCards >= 10,
      progress: Math.min(stats?.totalCards || 0, 10),
      total: 10,
    },
    {
      title: 'Master Mind',
      description: 'Mastered 5 cards',
      icon: TrophyIcon,
      earned: stats?.boxStats?.[4]?.count >= 5,
      progress: Math.min(stats?.boxStats?.[4]?.count || 0, 5),
      total: 5,
    },
    {
      title: 'Dedicated Student',
      description: 'Completed 7 days streak',
      icon: FireIcon,
      earned: false,
      progress: 4,
      total: 7,
    },
  ];

  const StatCard = ({ title, value, icon: Icon, color }) => (
    <motion.div
      whileHover={{ scale: 1.02 }}
      className={`p-6 bg-white dark:bg-gray-800 rounded-xl shadow-sm`}
    >
      <div className="flex items-center space-x-4">
        <div className={`p-3 ${color} rounded-lg`}>
          <Icon className="w-6 h-6 text-white" />
        </div>
        <div>
          <p className="text-sm text-gray-500 dark:text-gray-400">{title}</p>
          <p className="text-2xl font-semibold text-gray-900 dark:text-white">
            {value}
          </p>
        </div>
      </div>
    </motion.div>
  );

  const tabs = [
    { id: 'overview', label: 'Overview' },
    { id: 'achievements', label: 'Achievements' },
    { id: 'activity', label: 'Activity' },
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[50vh]">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
          className="w-8 h-8 border-4 border-primary-500 border-t-transparent rounded-full"
        />
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center space-x-6">
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            className="w-24 h-24 rounded-full bg-gradient-to-br from-primary-500 to-primary-600 flex items-center justify-center text-white text-4xl font-bold shadow-lg"
          >
            {user?.username[0].toUpperCase()}
          </motion.div>
          <div>
            <motion.h1
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="text-3xl font-bold text-gray-900 dark:text-white"
            >
              {user?.username}
            </motion.h1>
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.1 }}
              className="text-gray-500 dark:text-gray-400"
            >
              {user?.email}
            </motion.p>
          </div>
        </div>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <StatCard
          title="Total Cards"
          value={stats?.totalCards || 0}
          icon={AcademicCapIcon}
          color="bg-blue-500"
        />
        <StatCard
          title="Due Today"
          value={stats?.dueToday || 0}
          icon={ClockIcon}
          color="bg-green-500"
        />
        <StatCard
          title="Mastered"
          value={stats?.boxStats?.[4]?.count || 0}
          icon={TrophyIcon}
          color="bg-purple-500"
        />
        <StatCard
          title="Success Rate"
          value={`${Math.round(((stats?.boxStats?.[4]?.count || 0) / (stats?.totalCards || 1)) * 100)}%`}
          icon={ChartBarIcon}
          color="bg-orange-500"
        />
      </div>

      {/* Tabs */}
      <div className="mb-8">
        <div className="border-b border-gray-200 dark:border-gray-700">
          <nav className="flex space-x-8">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => setSelectedTab(tab.id)}
                className={`py-4 px-1 relative ${
                  selectedTab === tab.id
                    ? 'text-primary-600 dark:text-primary-400'
                    : 'text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300'
                }`}
              >
                {tab.label}
                {selectedTab === tab.id && (
                  <motion.div
                    layoutId="activeTab"
                    className="absolute bottom-0 left-0 right-0 h-0.5 bg-primary-500"
                  />
                )}
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Tab Content */}
      <AnimatePresence mode="wait">
        <motion.div
          key={selectedTab}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          transition={{ duration: 0.2 }}
        >
          {selectedTab === 'overview' && (
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {/* Learning Progress */}
              <div className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow-sm">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-4">
                  Learning Progress
                </h3>
                <div className="grid grid-cols-5 gap-4">
                  {stats?.boxStats?.map((box) => (
                    <div key={box.box} className="text-center">
                      <div className="mb-2">
                        <div className="relative mx-auto h-32 w-4">
                          <motion.div
                            initial={{ height: '0%' }}
                            animate={{ height: `${(box.count / (stats.totalCards || 1)) * 100}%` }}
                            className="absolute bottom-0 w-full bg-gradient-to-t from-primary-600 to-primary-400 rounded-full"
                          />
                          <div className="absolute inset-0 bg-gray-200 dark:bg-gray-700 -z-10 rounded-full" />
                        </div>
                      </div>
                      <p className="text-sm font-medium text-gray-900 dark:text-white">
                        Box {box.box}
                      </p>
                      <p className="text-xs text-gray-500 dark:text-gray-400">
                        {box.count} cards
                      </p>
                    </div>
                  ))}
                </div>
              </div>

              {/* Recent Activity */}
              <div className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow-sm">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-4">
                  Recent Activity
                </h3>
                <div className="space-y-4">
                  {[1, 2, 3].map((_, index) => (
                    <div
                      key={index}
                      className="flex items-center p-3 bg-gray-50 dark:bg-gray-700 rounded-lg"
                    >
                      <CalendarIcon className="w-5 h-5 text-gray-400 mr-3" />
                      <div>
                        <p className="text-sm text-gray-900 dark:text-white">
                          Reviewed 5 cards
                        </p>
                        <p className="text-xs text-gray-500 dark:text-gray-400">
                          2 hours ago
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}

          {selectedTab === 'achievements' && (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {achievements.map((achievement) => (
                <motion.div
                  key={achievement.title}
                  whileHover={{ scale: 1.02 }}
                  className={`p-6 bg-white dark:bg-gray-800 rounded-xl shadow-sm ${
                    achievement.earned ? 'border-2 border-primary-500' : ''
                  }`}
                >
                  <div className="flex items-start space-x-4">
                    <div className={`p-3 ${
                      achievement.earned ? 'bg-primary-100 dark:bg-primary-900' : 'bg-gray-100 dark:bg-gray-700'
                    } rounded-lg`}>
                      <achievement.icon className={`w-6 h-6 ${
                        achievement.earned ? 'text-primary-600 dark:text-primary-400' : 'text-gray-500 dark:text-gray-400'
                      }`} />
                    </div>
                    <div className="flex-1">
                      <h4 className="text-lg font-medium text-gray-900 dark:text-white">
                        {achievement.title}
                      </h4>
                      <p className="text-sm text-gray-500 dark:text-gray-400 mb-4">
                        {achievement.description}
                      </p>
                      <div className="relative h-2 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden">
                        <motion.div
                          initial={{ width: 0 }}
                          animate={{ width: `${(achievement.progress / achievement.total) * 100}%` }}
                          className="absolute h-full bg-primary-500"
                        />
                      </div>
                      <p className="mt-2 text-xs text-gray-500 dark:text-gray-400">
                        Progress: {achievement.progress}/{achievement.total}
                      </p>
                    </div>
                  </div>
                </motion.div>
              ))}
            </div>
          )}

          {selectedTab === 'activity' && (
            <div className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow-sm">
              <div className="space-y-6">
                {[1, 2, 3, 4, 5].map((_, index) => (
                  <div
                    key={index}
                    className="flex items-start space-x-4 p-4 bg-gray-50 dark:bg-gray-700 rounded-lg"
                  >
                    <div className="p-2 bg-primary-100 dark:bg-primary-900 rounded-lg">
                      <CalendarIcon className="w-5 h-5 text-primary-600 dark:text-primary-400" />
                    </div>
                    <div>
                      <p className="text-gray-900 dark:text-white">
                        Completed daily review
                      </p>
                      <p className="text-sm text-gray-500 dark:text-gray-400">
                        Reviewed 10 cards â€¢ 80% success rate
                      </p>
                      <p className="text-xs text-gray-400 dark:text-gray-500 mt-1">
                        {index + 1} day{index === 0 ? '' : 's'} ago
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </motion.div>
      </AnimatePresence>
    </div>
  );
};

export default Profile;
